import axios from "axios";
import { useState, useEffect } from "react"
import { Table ,Button} from 'reactstrap';
import clientServices from "../services/ClientService";
function ClientList (){
    const t1 =[1,2]
    const t2=t1.map((elem,i)=>{
        return elem +1;
    })
    const [clients, setClients] = useState([])
  async function getClients(){
       const result=  await  axios.get("http://localhost:3222/Clients")
       setClients(result.data)
       console.log(result.data)
    }
    useEffect (()=>{
        getClients();
    },[])

    async function deleteclient(id){
    //clientServices.deleteClient(id);
    await axios.http.delete(`/Clients/${id}`)
    

    getClients();
    }

   return ( 
    <div className="col">
       <h1>Liste des clients</h1>
        <Table bordered>
        <thead>
            <tr>
                <th>#</th>
                <th>last name</th>
                <th>first name</th>
                <th>cin</th>
                <th>adress</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
          {clients.map((elem,i)=>{
                return   <tr key={i}>
                            <td>{i+1}</td>
                            <td>{elem.last_name}</td>
                            <td>{elem.first_name}</td>
                            <td>{elem.cin}</td>
                            <td>{elem.adress}</td>
                            <td>
                                 <Button  onClick={()=>deleteclient()}  color="danger">Supprimer</Button>{' '}
                                 <Button color="success"><i class="fas fa-edit"></i>Modifier</Button>{' '}
                            </td>
                          </tr>
          })}
        </tbody>
        <tfoot>
            <tr>
                <th>#</th>
                <th>last name</th>
                <th>first name</th>
                <th>cin</th>
                <th>adress</th>
                <th>Action</th>
             </tr>
        </tfoot>
    </Table>
    </div>
   
)}

export default ClientList;
