import { useEffect, useState,Link } from "react"
import React from 'react';

import  CategorieServices from "../services/CategorieServices"
import {  useParams } from "react-router-dom"
import ProductServices from "../services/BookServices"
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
faEdit,
} from "@fortawesome/free-solid-svg-icons";
import { 
    Button, 
    Card, 
    CardBody,
    CardTitle,
    CardText,
    ListGroupItem,
    CardLink,
    ListGroup
} from "reactstrap";

function DetailBook() {
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [isbn, setISBN] = useState("")
    const [price, setPrice] = useState(0)
    const [selectedCategorie, setSelectedCategorie] = useState(0)
    const [categories, setCategories] = useState([])
    const [author,setAuthor] = useState("")
    const [editor,setEditor] = useState("")
    const [edition_date,setEditionDate] = useState("")
    const [image,setImage] = useState("")
    const {idP} = useParams()
    const [selectedCat, setSelectedCat]=useState(0)
    const navigation = useNavigate()
    useEffect (()=>{
                    getBook()
                    getCategories()
                   }
    ,[])
    async function getBook(){
        const res = await ProductServices.getBookBayId(idP)
   
        setName(res.data.name)
        setDescription(res.data.description)
        setISBN(res.data.isbn)
        setPrice(res.data.price)
        setAuthor(res.data.author)
        setEditor(res.data.editor)
        setImage(res.data.image)
        setEditionDate(res.data.edition_date)
        
}
    return (
        <Card
        style={{
          width: '18rem'
        }}
      >
        <img
          alt="Card"
          src={(`/assets/images/${image}`)}
        />
        <CardBody>
          <CardTitle tag="h5">
          {name}
          </CardTitle>
          <CardText>
          {description}
          </CardText>
        </CardBody>
        <ListGroup flush>
          <ListGroupItem>
            {author}
          </ListGroupItem>
          <ListGroupItem>
            {editor}
          </ListGroupItem>
          <ListGroupItem>
            {edition_date}
          </ListGroupItem>
        </ListGroup>
        <CardBody>
          <CardLink href="#">
            Card Link
          </CardLink>
          <CardLink href="#">
            Another Card Link
          </CardLink>
        </CardBody>
      </Card>
    )
}
export default DetailBook;
