import axios  from "axios";

const http1 = axios.create({
    baseURL:"http://localhost:3222",
    Headers:{"content-type": "application/json"}
})
async function getAllClient (){

    return await axios.http1.get("/Clients");
}
async function getClientBayId (idC){

    return await axios.http.get(`/Clients/${idC}`)
}
async function deleteClient (idC){

    return await axios.http.delete(`/Clients/${idC}`)
}
async function addClient (client){

    return await axios.http.post(`/Clients`,client)
}
const clientServices = {getAllClient, getClientBayId,deleteClient,addClient}

export default clientServices;
