import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser,faUsers,faShoppingCart} from '@fortawesome/free-solid-svg-icons'
import {Navbar,NavbarBrand,NavbarToggler} from 'reactstrap'
import { useState } from 'react'
//  <NavbarBrand href="/Clients/Add"><FontAwesomeIcon icon={faAdd}/> Nouveau Client</NavbarBrand>
function  Header(){
    const [toggle, setToggle] = useState(true)

    return  <header>
         <NavbarToggler   onClick={() => setToggle(!toggle)} />
              {
                (toggle && 
                <Navbar dark color="primary">
                    <div className="container">
                   
                        <NavbarBrand href="/"><FontAwesomeIcon icon={faUser}/> Espace Administrateur</NavbarBrand>
                        <NavbarBrand href="/Clients"><FontAwesomeIcon icon={faUsers}/> Clients</NavbarBrand>
                        <NavbarBrand href="/Commande/Add"><FontAwesomeIcon icon={faShoppingCart}/>Commandes</NavbarBrand>
                        <NavbarBrand href="/Produits"><FontAwesomeIcon icon={faShoppingCart}/>Produits</NavbarBrand>
                        <NavbarBrand href="/Categories"><FontAwesomeIcon icon={faShoppingCart}/>Catégories</NavbarBrand>       
                    </div>
                </Navbar> 
                )
            }
       
            </header>
            
}
export default Header;
