import http from "./http-common"
async function getAllClient (){

    return await axios.http.get("/Clients");
}

async function getClientBayId (idC){

    return await axios.http.get(`/Clients/${idC}`);
}
async function deleteClient (idC){

    return await axios.http.delete(`/Clients/${idC}`);
}

const clientServices = {getAllClient, getClientBayId,deleteClient}

export default clientServices;
