import { useEffect, useState } from "react"
import React from 'react';
import { Button, Form, FormGroup, Input,Label } from 'reactstrap';
import  CategorieServices from "../../services/CategorieServices";
import { useNavigate } from "react-router-dom";
import BookServices from "../../services/BookServices";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAdd } from "@fortawesome/free-solid-svg-icons";
function NewBook() {
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [isbn, setIsbn] = useState("")
    const [author,setAuthor] = useState("")
    const [editor,setEditor] = useState("")
    const [edition_date,setEditionDate] = useState("")
    const [image,setImage] = useState("")
    const [selectedCategorie, setSelectedCategorie] = useState(0)
    const [quantite, setQuantite] = useState(0)
    const [categories, setCategories] = useState([])
    const naviger = useNavigate()
    useEffect (()=>{getCategories();},[])
    async function getCategories(){
       const result = await CategorieServices.getAllCategories();
       setCategories(result.data)
    }
    async function newProduit(e) {
        e.preventDefault()
        const book = { "_id":idP ,"name": name, "description": description, "isbn": isbn,"author":author, "editor":editor,
        "edition_date":edition_date,
        "price": price ,
        "image":image,
        "categorie":categories[selectedCategorie]}
        await BookServices.addProduit(produit);
         naviger("/Produits")
    }
    return (
        <div className="container">
            <h3 className="title">Nouveau Produit</h3>
            <Form className="form" onSubmit={(e) => newProduit(e)}>
                <FormGroup className="col-sm">
                    <Input id="code" type="text"
                        onChange={(e) => setCode(e.target.value)} placeholder="Entrer code..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="nom" type="text"
                        onChange={(e) => setNom(e.target.value)} placeholder="Entrer nom..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="description" type="text"
                        onChange={(e) => setDescription(e.target.value)} placeholder="Entrer description..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="prixUnitaire" type="text"
                        onChange={(e) => setPrixUnitaire(e.target.value)} placeholder="Entrer prixUnitaire..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="prixUnitaire" type="text"
                        onChange={(e) => setQuantite(e.target.value)} placeholder="Entrer quantite..." />
                </FormGroup>
                <FormGroup  className="col-sm">
                    <Label for="exampleSelect">Selectionnez une catégorie</Label>
                    <Input type="select" name="select" id="exampleSelect" onChange={(e) => setSelectedCategorie(e.target.value)}>
                            {
                                categories.map((elem,index)=>{
                                     return <option  key={index} value={index}>{elem.nom}</option>
                                })
                            }        
                    </Input>
                </FormGroup>
                
                <Button className ="" type="submit" color="primary">  
                  <FontAwesomeIcon  icon={faAdd}/> <span>Add</span>
                </Button>{' '}
            </Form>
        </div>
    )
}
export default NewBook;
