import axios from "axios";
import { useState, useEffect } from "react"
import { Table } from 'reactstrap';
import ClientServices from "../services/ClientService";
function ClientList (){
    const t1 =[1,2]
    const t2=t1.map((elem,i)=>{
        return elem +1;
    })
     
    const [clients, setClients] = useState([])
  async function getClients(){
       const result=ClientServices.getAllClient()
       setClients(result.data)
       console.log(result.data)
    }
    useEffect (()=>{
        getClients();
    },[])
return ( 
    <div className="container">
    <Table>
        <thead>
            <tr>
                <th>#</th>
                <th>last name</th>
                <th>first name</th>
                <th>cin</th>
            </tr>
        </thead>
        <tbody>
          {clients.map((elem,i)=>{
                return <tr key={i}><td>{i+1}</td><td>{elem.last_name}</td><td>{elem.first_name}</td><td>{elem.cin}</td></tr>
          })}
        </tbody>
    </Table>
    </div>
)}

export default ClientList;
