import { useEffect, useState } from "react"
import React from 'react';
import { Button, Form, FormGroup, Input,Label } from 'reactstrap'
import  CategorieServices from "../../services/CategorieServices"
import {  useParams } from "react-router-dom"
import ProductServices from "../../services/ProduitServices"
import { useNavigate } from "react-router-dom";
function EditProduit() {
    const [code, setCode] = useState("")
    const [nom, setNom] = useState("")
    const [description, setDescription] = useState("")
    const [prixUnitaire, setPrixUnitaire] = useState(0)
    const [selectedCategorie, setSelectedCategorie] = useState(0)
    const [categories, setCategories] = useState([])
    const [quantite,setQuantite] = useState(0)
    const {idP} = useParams()
    const [selectedCat, setSelectedCat]=useState(0)
    const navigation = useNavigate()
    useEffect (()=>{
                    getProduit()
                    getCategories()
                   }
    ,[])
    async function getProduit(){
        const res = await ProductServices.getProduitBayId(idP)
        setCode(res.data.code)
        setNom(res.data.nom)
        setDescription(res.data.description)
        setPrixUnitaire(res.data.prixUnitaire)
        setQuantite(res.data.quantite)
        setSelectedCat(res.data.categorie._id)
        setSelectedCategorie(res.data.categorie.nom)
}
    async function getCategories(){
       const result = await CategorieServices.getAllCategories();
       setCategories(result.data)
    }
    async function updateProduit(e) {
        e.preventDefault()
        const produit = { "_id":idP ,"code": code, "nom": nom, "description": description, "prixUnitaire": prixUnitaire ,"quantite":quantite,
        "categorie":categories[selectedCategorie]}
        const res=await ProductServices.updateProduit(idP,produit)
        navigation("/Produits")
    }
    return (
        <div className="container">
            <h3 className="title">Editer Produit</h3>
            <Form className="form" onSubmit={(e) => updateProduit(e)}>
                <FormGroup className="col-sm">
                    <Input id="code" type="text"
                         value={code}
                         onChange={(e) => setCode(e.target.value)} placeholder="Entrer code..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="nom" type="text"
                         value={nom}
                         onChange={(e) => setNom(e.target.value)} placeholder="Entrer nom..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="description" type="text"
                          value={description} 
                          onChange={(e) => setDescription(e.target.value)} placeholder="Entrer description..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="prixUnitaire" type="text"
                           value={prixUnitaire} 
                           onChange={(e) => setPrixUnitaire(e.target.value)} placeholder="Entrer  prixUnitaire..." />
                </FormGroup>
                <FormGroup className="col-sm">
                    <Input id="prixUnitaire" type="number"
                           value={quantite} 
                           onChange={(e) => setQuantite(e.target.value)} placeholder="Entrer  prixQuantite..." />
                </FormGroup>
                <FormGroup  className="col-sm">
                    <Label for="exampleSelect">Selectionnez une catégorie</Label>
                    <Input type="select" name="select" id="exampleSelect" onChange={(e) => setSelectedCategorie(e.target.value)}>
                            {
                                     categories.map((elem,index)=>{
                                     var selected = (selectedCat === elem._id) ? true : false;
                                     return <option  key={index} value={index} defaultValue={selectedCategorie} >{elem.nom}</option>
                                })
                            }        
                    </Input>
                </FormGroup>
                   <Button className ="" type="submit" color="primary">Enregistrer</Button>{' '}
            </Form>
        </div>
    )
}
export default EditProduit;
