import BookServices from "../../services/BookServices"
import { useState, useEffect } from "react"
import {  useParams } from "react-router-dom"
import CategorieServices from "../../services/CategorieServices";
import { 
    Button, 
    Card, 
    CardBody,
    CardTitle,
    CardSubtitle,
    Row,CardImg
} from "reactstrap";
import React from "react";
import { faAdmini,faPaperPlane } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { Link } from "react-router-dom";
function CardBookCategory (){
  const [Book, setBook] = useState([])
  const [categorie,setCategorie] =useState("")
  const {idCatg} = useParams()
  async function getBook(){
     const result = await BookServices.getBookCategory('63fde77bf716bfabcff7488c');
     if(result) console.log("yes")
     setBook(result.data)
  }
  async function getCategorie(){
    const res = await CategorieServices.getCategorieBayId(idCateg)
    setCategorie(res.data.nom)
}
   useEffect (()=>{getBook(),getCategorie;},[])
    return (
    <React.Fragment>
    <div id ="explorer">
    <h1>{categorie}</h1>
    <Row>
    {Book.map((elem,i)=>{
           
      return <Card className="bg-light border-top mt-2  border-warning"
              key={i} 
              style={{
                width: '14rem',
                margin: "8px",
                height: "24rem"
              }}
               >
              <CardImg
              style={{
                    width: '11rem',
                    height:'11rem',
                    margin:'21px'
              }}
              const a = {elem.image}
                alt="Sample"
                src={(`/assets/images/${elem.image}`)}
              />
              <CardBody>
                <CardTitle tag="h5">
                  {elem.name}
                </CardTitle>
                <CardSubtitle
                  className="mb-2 text-muted"
                  tag="h6">
                 Price : {elem.price} $
                </CardSubtitle>
                <Link  tag={Link} to={`detail/${elem._id}`}>
                <Button color="primary">
                  Details
                </Button>
                </Link>
 
              </CardBody>
            </Card>
    })}
      </Row>
      </div>
      </React.Fragment>
    )
         
}export default CardBookCategory;
