import { useState } from "react"
import { Button, Form, FormGroup, Input,
    DropdownItem,
    DropdownMenu,
    Dropdown,
    DropdownToggle
} from 'reactstrap';
import CategorieServices from "../../services/CategorieServices";
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAdd } from "@fortawesome/free-solid-svg-icons";
function SearchCategory()
{
    const [isOpenDropdown, setIsOpenDropDown] = useState(false)

    const [categories, setCategories] = useState([])
    async function getCategories(){
       const result = await CategorieServices.getAllCategories();
       setCategories(result.data)
    }
    useEffect (()=>{getCategories();},[])//executer un traitement apès avoir télécharger le composante
    async function deleteCategorie(id){ 
                              await  CategorieServices.deleteCategorie(id)
                               getCategories()}
    return (
        <Dropdown  toggle={() => setIsOpenDropDown(!isOpenDropdown)} isOpen={isOpenDropdown}>
        <DropdownToggle>
        <Input  placeholder="search" />
        </DropdownToggle>
        <DropdownMenu>
        <DropdownItem>Romance</DropdownItem>
        <DropdownItem>Literature & Fiction</DropdownItem>
        <DropdownItem text>Science Fiction & Fantasy</DropdownItem>
        <DropdownItem >Biographies & Memoirs</DropdownItem>
        <DropdownItem >Children's Books</DropdownItem>
        </DropdownMenu>
        </Dropdown>
    )
}
export default SearchCategory;
