import axios from "axios";
import { useState, useEffect } from "react"
import { Table ,Button} from 'reactstrap';
import clientServices from "../services/ClientService";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faRemove,faEdit } from '@fortawesome/free-solid-svg-icons'

    //abdelmajid.bousselham@hotmal.com 
function ClientList (){
    
    const [clients, setClients] = useState([])
  async function getClients(){
       //const result=  await  axios.get("http://localhost:3222/Clients")
      const result = await clientServices.getAllClient();
       setClients(result.data)
       console.log(result.data)
    }
    useEffect (()=>{
        getClients();
    },[])

    async function deleteclient(id){
        const result = await  axios.delete(`http://localhost:3222/Clients/${id}`)
        //getClients();
    }

   return ( 
    <div className="col">
       <h1>Liste des clients</h1>
        <Table bordered>
        <thead>
            <tr>
                <th>#</th>
                <th>last name</th>
                <th>first name</th>
                <th>cin</th>
                <th>adress</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
          {clients.map((elem,i)=>{
                return   <tr key={i}>
                            <td>{i+1}</td>
                            <td>{elem.last_name}</td>
                            <td>{elem.first_name}</td>
                            <td>{elem.cin}</td>
                            <td>{elem.adress}</td>
                            <td>
                                 <Button  onClick={()=>deleteclient(elem._id)}  color="danger">
                                   Delete
                                 </Button>{' '}
                                 <Link className="edit" to={`/Clients/edit/${elem._id}`}>
                              
                                    <Button color="success">
                                    <FontAwesomeIcon icon={faEdit}/>Edit
                                    </Button>{' '}</Link>
                            </td>
                          </tr>
          })}
        </tbody>
        <tfoot>
            <tr>
                <th>#</th>
                <th>last name</th>
                <th>first name</th>
                <th>cin</th>
                <th>adress</th>
                <th>Action</th>
             </tr>
        </tfoot>
    </Table>
    </div>
   
)}

export default ClientList;
