import axios  from "axios";
async function getAllBook(){

    return await axios.get("http://localhost:3222/Book");
}
async function getBookBayId (idC){
    return await axios.get(`http://localhost:3222/Book/${idC}`)
}
async function deleteBook (idC){

    return await axios.delete(`http://localhost:3222/Book/${idC}`)
}
async function addBook (book){

    return await axios.post(`http://localhost:3222/Book`,book)
}
async function updateBook(idP,book)
{
    return   await axios.put(`http://localhost:3222/Book/${idP}`,book)
}
const Bookervices = {getAllBook, getBookBayId,deleteBook,addBook,updateBook}

