import { useEffect, useState,useNavigate,Link } from "react"
import React from 'react';
import Bookervices from "../services/BookServices"
import {  useParams } from "react-router-dom"
import { 
    Button, 
    Card, 
    CardBody,
    CardTitle,
    CardSubtitle
} from "reactstrap";
function DetailBook(){
    
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [isbn, setISBN] = useState("")
    const [price, setPrice] = useState(0)
    const [author,setAuthor] = useState("")
    const [editor,setEditor] = useState("")
    const [edition_date,setEditionDate] = useState("")
    const [image,setImage] = useState("")
    const navigation = useNavigate()
    const {idP} = useParams()
        useEffect (()=>{
                         getDetailBook()
                        },[])
                        
        async function getDetailBook(){
        const res = await Bookervices.getBookBayId(idP)
        setName(res.data.name)
        setDescription(res.data.description)
        setISBN(res.data.isbn)
        setPrice(res.data.price)
        setAuthor(res.data.author)
        setEditor(res.data.editor)
        setImage(res.data.image)
        setEditionDate(res.data.edition_date)
}
   return (
            <Card
            style={{
            width: '14rem',
            margin: "8px",
            height: "25rem"
            }}
            >
            <img
            style={{
                width: '14rem',
                height:'14rem'
            }}
            alt="Sample"
            src={(`/assets/images/${image}`)}
            />
            <CardBody>
            <CardTitle tag="h5">
                {name} {author} {edition_date} {isbn} {description}
            </CardTitle>
            <CardSubtitle
                className="mb-2 text-muted"
                tag="h6">
            Price : {price} $ {editor}
            </CardSubtitle>
            <Link to={`addToCart/${idP}`}>
            <Button color="primary">
                Add To cart
            </Button>
            </Link>

            </CardBody>
        </Card>
   )
    
}
export default DetailBook
