import { useState } from "react"
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import clientServices from "../services/ClientService";
function AddClient()
{
    const [last_name, setFirstName] = useState("");
    const [first_name, setLastName] = useState("");
    const [cin, setCin] = useState("");
    const [adress, setAdress] = useState("");
    async function addClient()
    {
      const client = {last_name,first_name,cin,adress}
      clientServices.addClient(client)
    }
                ///<Label for="">Non</Label>
                // <Label for="">Prénom</Label>
                //<Label for="">Adress</Label>
                // <Label for="">Prénom</Label>
    console.log(last_name)
    console.log(first_name)
    console.log(last_name)
    return (
      <div className="container">
         <h1>Nouveau client</h1>
      <Form onSubmit={()=>addClient()}>
        <FormGroup className="col-sm">
            <Input  type="text" 
                  value={last_name}
                  onChange={(e) => setFirstName(e.target.value)} placeholder="Entrer Nom..." />
        </FormGroup>

        <FormGroup className="col-sm">
           
              <Input  type="text" 
                    value={first_name}
                    onChange={(e) => setLastName(e.target.value)}placeholder="Entrer Prénom..." />
        </FormGroup>

        <FormGroup className="col-sm">
           
                <Input   type="text" 
                        value={cin}
                        onChange={(e) => setCin(e.target.value)} placeholder="Entrer CIN..." />
        </FormGroup>


        <FormGroup className="col-sm">
            
                <Input   type="text" 
                         value={cin}
                        onChange={(e) => setAdress(e.target.value)} placeholder="Entrer Adress..." />
         </FormGroup>
            <Button type="submit" color="primary">Ajouter+</Button>{' '}
        </Form>
      </div>
      
    )
}

export default AddClient;
