        //e.preventDefault()
        const produit = { "code": code, "nom": nom, "description": description, "etat": prixUnitaire }
        await ProductServices.addProduit(produit);
        // naviger("/Commandes")
    }
    return (
        <div className="container">
            <h1 className="title">Nouveau Commande</h1>
            <Form className="form" onSubmit={() => newProduit()}>
                <FormGroup className="col-sm-6">
                    <Input id="code" type="text"
                        onChange={(e) => setCode(e.target.value)} placeholder="Entrer code produit..." />
                </FormGroup>
                <FormGroup className="col-sm-6">
                    <Input id="nom" type="text"
                        onChange={(e) => setNom(e.target.value)} placeholder="Entrer nom..." />
                </FormGroup>
                <FormGroup className="col-sm-6">
                    <Input id="description" type="text"
                        onChange={(e) => setDescription(e.target.value)} placeholder="Entrer description..." />
                </FormGroup>
                <FormGroup className="col-sm-6">
                    <Input id="prixUnitaire" type="text"
                        onChange={(e) => setPrixUnitaire(e.target.value)} placeholder="Entrer prixUnitaire..." />
                </FormGroup>

