import BookServices from "../services/BookServices"
import { 
    Button, 
    Card, 
    CardBody,
    CardTitle,
    CardSubtitle,
    CardText, 
    Row,
    CardImg
} from "reactstrap";
import React from "react";
import { faAdmini,faPaperPlane } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
function CardBook (){
  const [Book, setBook] = useState([])
  async function getBook(){
     const result = await BookServices.getAllBook();
     setBook(result.data)
  }
  useEffect (()=>{getBook();},[])
    return (
    <React.Fragment>
    <Row >
    {Book.map((elem,i)=>{
      return <Card
              key={i} 
              style={{
                width: '14rem',
                margin: "8px",
                height: "25rem"
              }}
            >
              <img
              style={{
                    width: '14rem',
                    height:'14rem'
              }}
                alt="Sample"
                src="/assets/images/ThingWillNeverGotOver.jpg"
              />
              <CardBody>
                <CardTitle tag="h5">
                {elem.name}
                </CardTitle>
                <CardSubtitle
                  className="mb-2 text-muted"
                  tag="h6">
                  Price : 500
                </CardSubtitle>
                <Button color="primary">
                  Details
                </Button>
              </CardBody>
            </Card>
    })}
      </Row>
      </React.Fragment>
    )
         
}export default CardBook;
