import { useEffect, useState,Link } from "react"
import React from 'react';

import  CategorieServices from "../services/CategorieServices"
import {  useParams } from "react-router-dom"
import ProductServices from "../services/BookServices"
import { useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart} from '@fortawesome/free-solid-svg-icons'
import {
faEdit,
} from "@fortawesome/free-solid-svg-icons";
import { 
    Button, 
    Card, 
    CardBody,
    CardTitle,
    CardText,
    ListGroupItem,
    CardLink,
    ListGroup,
    CardImg
} from "reactstrap";

function DetailBook() {
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [isbn, setISBN] = useState("")
    const [price, setPrice] = useState(0)
    const [author,setAuthor] = useState("")
    const [editor,setEditor] = useState("")
    const [edition_date,setEditionDate] = useState("")
    const [image,setImage] = useState("")
    const {idP} = useParams()
    const navigation = useNavigate()
    useEffect (()=>{
                    getBook()
                   }
    ,[])
    async function getBook(){
        const res = await ProductServices.getBookBayId(idP)
   
        setName(res.data.name)
        setDescription(res.data.description)
        setISBN(res.data.isbn)
        setPrice(res.data.price)
        setAuthor(res.data.author)
        setEditor(res.data.editor)
        setImage(res.data.image)
        setEditionDate(res.data.edition_date)
}
    return (
     <>
        <img
                  style={{
                        width: '14rem',
                        height:'14rem',
                        margin:'21px'
                  }}
                  alt="Card"
                  src={(`/assets/images/${image}`)}
        />

             <Button color='warning'><FontAwesomeIcon icon={faShoppingCart}  className="mr-2" /> Add to cart</Button> 
             </>
    )
}
export default DetailBook;
