import { useParams } from "react-router-dom";
import { useState, useEffect } from "react"
import { Button, Form, FormGroup, Input, FormText } from 'reactstrap';
import clientServices from "../services/ClientService";
function EditClient (){
        const [last_name, setFirstName] = useState("");    
        
        const [first_name, setLastName] = useState("");
        const [cin, setCin] = useState("")
        const [adress, setAdress] = useState("")
    const {idc} = useParams();
    return <h1>Edit Client{idc}</h1>
async function getClient(){
    const rep =  await clientServices.getClientBayId(idc)
}
useEffect (()=>{
    EditClient()   
},[])

return (
    <div className="container">
       <h1>Nouveau client</h1>
        <Form onSubmit={()=>newClient()}> 
          <FormGroup className="col-sm">
              <Input    id="lastName" type="text" 
                        value={last_name}
                        onChange={(e) => setFirstName(e.target.value)} placeholder="Entrer Nom..." />
          </FormGroup>
          <FormGroup className="col-sm">
                <Input   id="firstName" type="text" 
                        value={first_name}
                        onChange={(e) => setLastName(e.target.value)}placeholder="Entrer Prénom..." />
          </FormGroup>
          <FormGroup className="col-sm">
                  <Input  id="cin"  type="text" 
                          value={cin}
                          onChange={(e) => setCin(e.target.value)} placeholder="Entrer CIN..." />
          </FormGroup>
          <FormGroup className="col-sm">
                  <Input   id="adress" type="text" 
                          value={adress}
                          onChange={(e) => setAdress(e.target.value)} placeholder="Entrer Adress..." />
          </FormGroup>
              <Button type="submit" color="primary">Ajouter+</Button>{' '}
        </Form>
    </div>
  )

}
export default EditClient;
