import { useEffect, useState,useN } from "react"
import React from 'react';
import Bookervices from "../services/BookServices"
import {  useParams } from "react-router-dom"

function DetailBook(){
    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [isbn, setISBN] = useState("")
    const [price, setPrice] = useState(0)
    const [author,setAuthor] = useState("")
    const [editor,setEditor] = useState("")
    const [edition_date,setEditionDate] = useState("")
    const [image,setImage] = useState("")
    const navigation = useNavigate()
    const {idBook} = useParams()
        useEffect (()=>{getDetailBook()},[])
        async function getDetailBook(){
        const res = await Bookervices.getBookBayId(idBook)
        setName(res.data.name)
        setDescription(res.data.description)
        setISBN(res.data.isbn)
        setPrice(res.data.price)
        setAuthor(res.data.author)
        setEditor(res.data.editor)
        setImage(res.data.image)
        setEditionDate(res.data.edition_date)
}
   return (
            <Card
            key={i} 
            style={{
            width: '14rem',
            margin: "8px",
            height: "25rem"
            }}
            >
            <img
            style={{
                width: '14rem',
                height:'14rem'
            }}
            const a = {elem.image}
            alt="Sample"
            src={(`/assets/images/${elem.image}`)}
            />
            <CardBody>
            <CardTitle tag="h5">
                {elem.name}
            </CardTitle>
            <CardSubtitle
                className="mb-2 text-muted"
                tag="h6">
            Price : {elem.price} $
            </CardSubtitle>
            <Link to={`detail/${elem._id}`}>
            <Button color="primary">
                Details
            </Button>
            </Link>

            </CardBody>
        </Card>
   )
    
}
export default DetailBook
