    const {idP} = useParams()
    const [selectedCat, setSelectedCat]=useState(0)
    const navigation = useNavigate()
    useEffect (()=>{
                    getBook()
                    getCategories()
                   }
    ,[])
    async function getBook(){
        const res = await ProductServices.getBookBayId(idP)
   
        setName(res.data.name)
